import { StatusBar } from 'expo-status-bar';
import React, { useRef } from 'react';
import { StyleSheet, Text, TextInput, View, Button, ImageBackgroundBase, ImageBackground, Dimensions, FlatList } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

//import components
import deviceStorage from '../../services/deviceStorage';

export default function HomeScreen() {

// Generating dummy data
const data = [
  { noPlate: 'AB123', dateTime: '16 Sept/ 4:30pm', description: 'Last location at XXX' },
  { noPlate: 'WE635', dateTime: '10 Sept/ 2:00pm', description: 'Last location at XXX' },
  { noPlate: 'UO999', dateTime: '7 Sept/ 12:00pm', description: 'Last location at XXX' },
  { noPlate: 'AB368', dateTime: '6 Sept/ 2:30am', description: 'Last location at XXX' },
];
const DATA = Array.from({ length: 10 }, (v, i) => `List item ${i}`);
const myRef = useRef(null);


  return (
    <View style={styles.container}>
      
      {/*Top*/}
      <ImageBackground
        source={require('../../assets/header.png')}
        style={{
          height: Dimensions.get('window').height / 5.5, 
        }}>
        <View style={styles.topView}>
          <Text style={styles.title}>Top Feeds</Text>
        </View>
      </ImageBackground>
      <StatusBar style="auto" />

      
      <FlatList
        data={data}
        keyExtractor={item => item.noPlate}
        renderItem={({ item }) => (
          <View style={styles.listItem}>
            <Text>((GAMBAR)</Text>
            <Text style={{fontSize: 20, fontWeight: 'bold'}}>{item.noPlate}</Text>
            <Text style={{fontSize:18, opacity: .7}}>{item.dateTime}</Text>
            <Text style={{fontSize:14, opacity: .8}}>{item.description}</Text>
          </View>
        )}
      />

      {/*List Card*/}
      {/*<FlatList
        ref={myRef}
        getItemLayout={(data, index) => ({
          length: DATA.length,
          offset: DATA.length * index,
          index,
        })}
        data={DATA}
        renderItem={(itemData) => {
          return (
            <View style={styles.listItem}>
              <Text style={{fontSize: 20, fontWeight: 'bold'}}>{item.noPlate}</Text> 
              <Text style={{fontSize:18, opacity: .7}}>{item.dateTime}</Text>
              <Text style={{fontSize:14, opacity: .8}}>{item.description}</Text>
            </View>
          );
        }}
      />*/}

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  row: {
    flexDirection: 'row',
    flexWrap: 'wrap',
  },

  topView: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
    paddingBottom: 40,
  },

  title: {
    color: '#fff',
    fontSize: 40,
    fontWeight: 'bold',
  },

  listItem: {
    bshadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    borderWidth: 1,
    borderColor: '#333',
    marginBottom: 'spacing',
    padding: 20,
    borderRadius: 12,
    marginTop: 10,
    marginLeft: 15,
    marginRight: 15,
  },

  searchBox: {
    borderColor: '#4ed9b8',
    borderWidth: 2,
    height: 50,
    width: 300,
    paddingLeft: 20,
    margin: 5,
  },

});
