import { StatusBar } from 'expo-status-bar';
import React, { useRef } from 'react';
import { StyleSheet, Text, View, Button, ImageBackgroundBase, ImageBackground, Dimensions, FlatList } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

//import components
import deviceStorage from '../../services/deviceStorage';

export default function HomeScreen() {

  const plateNum = ['ABC123', 'WED265', 'VDE999', 'QWE830', 'OUB649'];
  const address = ['KL', 'Shah Alam', 'KL', 'Bangi', 'Shah Alam'];
  const timeDate = ['19 Sept/8:30pm', '10 Sept/2:00pm', '20 Aug/2:3pm', '15 Aug/12:00am', '10 Aug/4:00pm'];
  const description = ['Near building XXX', 'Last loc at Seksyen 7', 'Near building XXX', 'witnessed crime at XXX', 'Near building XXX'];
  
  // Generating dummy data
  const DATA = Array.from({ length: 100 }, (v, i) => `List item ${i}`);
  const myRef = useRef(null);
  return (
    <View style={styles.container}>
      
      {/*Top*/}
      <ImageBackground
        source={require('../../assets/header.png')}
        style={{
          height: Dimensions.get('window').height / 5.5, 
        }}>
        <View style={styles.topView}>
          <Text style={styles.title}>Top Feeds</Text>
        </View>
      </ImageBackground>
      <StatusBar style="auto" />

      {/*List Card*/}

      <FlatList style={{justifyContent: 'space-between',}}
        ref={myRef}
        getItemLayout={(data, index) => ({
          length: DATA.length,
          offset: DATA.length * index,
          index,
        })}
        data={DATA}
        renderItem={(itemData) => {
          return (
            <View style={styles.listItem}>
              <Text style={{fontSize: 20, fontWeight: 'bold'}}>No plate </Text> 
              <Text style={{fontSize:18, opacity: .7}}>Date/Time</Text>
              <Text style={{fontSize:14, opacity: .8}}>description</Text>
            </View>
          );
        }}
      />

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  row: {
    flexDirection: 'row',
    flexWrap: 'wrap',
  },

  topView: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
    paddingBottom: 40,
  },

  title: {
    color: '#fff',
    fontSize: 40,
    fontWeight: 'bold',
  },

  listItem: {
    backgroundColor: 'grey',
    borderWidth: 1,
    borderColor: '#333',
    marginBottom: 'spacing',
    padding: 'spacing',
    borderRadius: 12,
  }

});
